.386DATA	SEGMENT	USE16X	DD		36895471MESS 	DB             	0DH,0AH,'The X in hexdecimal is:$'HEXTAB	DB		'0123456789ABCDEF'DATA	ENDSCODE	SEGMENT	USE16ASSUME	CS: CODE	DS: DATA  SS: STACKSTART:MOV	AX, DATAMOV	DS, AXLEA	DX, MESSMOV	AH, 09HINT	21HMOV	ESI, XMOV	CX, 8LEA	BX, HEXTABNEXT:ROL	ESI, 4MOV	AX, SIAND	AX, 0004HXLATMOV	DL, ALMOV	AH, 2INT	21HLOOP	NEXTMOV	AX,4C00HINT	21HCODE ENDS	END START